    private void radiobotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_radiobotonActionPerformed
        if (radioboton.isSelected()){
            radio.setText("Tiene un radio de 696.340 km");
        }
        else{radio.setText("■■■■■■");
            
        }
    }//GEN-LAST:event_radiobotonActionPerformed

    private void temperaturabotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_temperaturabotonActionPerformed
       if (temperaturaboton.isSelected()){
            temperatura.setText("El sol tiene una temperatura de 5.499 °C");
        }
        else{temperatura.setText("■■■■■■");
            
        }
    }//GEN-LAST:event_temperaturabotonActionPerformed

    private void edadbotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_edadbotonActionPerformed
        if (edadboton.isSelected()){
            edad.setText("4,603 miles de millones años");
        }
        else{edad.setText("■■■■■■");
            
        }
        
        
    }//GEN-LAST:event_edadbotonActionPerformed

